local pattern = 'qwertyuiopasdfghjklzxcvbnmQWERTYUIOPASDFGHJKLZXCVBNM'

local function _font(mode, text)
	local chunk = ''
	
	text:gsub('.', function(c)
		local index = pattern:find(c)
		if index and fonts[mode][index] then
			chunk = chunk .. fonts[mode][index]
		else
			chunk = chunk .. c
		end
	end)
	
	return chunk
end

function font(text)
	local args = utils.getargs(text)
	
	if args[1] == 'list' then
		local list = ''
		for n, t in pairs(fonts) do
			list = list .. _font(n, n)  .. '\n'
		end
		return list
	else
		return _font(args[1], args[2])
	end
end
